/**
 * Input - Shared types and styles
 * This file contains the shared logic between web and native implementations
 */

/**
 * Base input styles that work with both Tailwind and NativeWind
 */
export const inputClassName =
  "flex h-9 w-full rounded-md border border-input bg-transparent px-3 py-1 text-sm shadow-sm transition-colors file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:cursor-not-allowed disabled:opacity-50";

/**
 * Base input props interface
 * Shared across web and native implementations
 */
export interface BaseInputProps {
  className?: string;
  value?: string;
  onChange?: (value: string) => void;
  placeholder?: string;
  disabled?: boolean;
  editable?: boolean;
}

/**
 * Input - Web implementation
 * Uses standard HTML input element
 */
import type { ComponentProps } from "react";

import { cn } from "@tocld/ui";
import { inputClassName } from "./input.js";

// Re-export shared types and styles
export type { BaseInputProps } from "./input.js";
export { inputClassName } from "./input.js";

export interface InputProps extends ComponentProps<"input"> {
  className?: string;
}

export function Input({ className, ...props }: InputProps) {
  return (
    <input className={cn(inputClassName, className)} {...props} />
  );
}

/**
 * Input - Native implementation
 * Uses React Native TextInput with NativeWind styling
 */
import * as React from "react";

import { TextInput } from "./primitives";
import { cn } from "@tocld/ui";
import { inputClassName, type BaseInputProps } from "./input";

export interface InputProps extends BaseInputProps {
  onChangeText?: (text: string) => void;
  secureTextEntry?: boolean;
  accessibilityLabel?: string;
  testID?: string;
}

export function Input({
  className,
  value,
  onChangeText,
  onChange,
  placeholder,
  disabled,
  editable = true,
  secureTextEntry,
  accessibilityLabel,
  testID,
}: InputProps) {
  return (
    <TextInput
      className={cn(inputClassName, className)}
      value={value}
      onChangeText={onChangeText || onChange}
      placeholder={placeholder}
      editable={!disabled && editable}
      secureTextEntry={secureTextEntry}
      accessibilityLabel={accessibilityLabel}
      testID={testID}
    />
  );
}
